import { Layout} from 'antd';
const { Footer } = Layout;
import { connect } from 'react-redux';

const PageFooter = () => {
    return (
        <div  style={ (this.props.isLoggedInStatus) ? {}: { position:"absolute", bottom:0, color: "blue", width:"100%"  } }>
            <Footer style={{ textAlign: 'center' }}> Developed with ❤️ by <a href="https://github.com/dineshsonachalam/GoAnyJSON" rel="noreferrer" target="_blank">Dinesh Sonachalam</a> © {(new Date().getFullYear())}</Footer>
        </div>
    );
}

// https://stackoverflow.com/a/50225424
